import React, { Component } from 'react';
import './App.css';

const INITIAL_STATE = {
  email: '',
  password: '',
  error: null,
};

class App extends Component {
  constructor(props) {
    super(props);

    this.state = { ...INITIAL_STATE };
  }
  onSubmit = async (event) => {
    event.preventDefault();

    // const { email, password } = this.state;

    // const error = await this.props.firebase.doSignInWithEmailAndPassword(
    //   email,
    //   password
    // );

    // if (error === null) {
    //   this.setState({ ...INITIAL_STATE });
    //   this.props.history.push(ROUTES.MY_COLLECTION);
    // } else {
    //   this.setState({ error });
    // }
  };

  onChange = (event) => {
    this.setState({ [event.target.name]: event.target.value, error: null });
  };

  render() {
    const { email, password } = this.state;

    return (
      <form onSubmit={this.onSubmit}>
        <input
          type='text'
          name='email'
          value={email}
          onChange={this.onChange}
          placeholder='Email Address'
        />
        <span data-placeholder='&#xf207;'></span>

        <input
          type='password'
          name='password'
          value={password}
          onChange={this.onChange}
          placeholder='Password'
        />
        <span data-placeholder='&#xf191;'></span>

        <label>
          Username:
          <input type='text' name='username' />
        </label>
        <label>
          Password:
          <input type='text' name='password' />
        </label>
        <input type='submit' value='Submit' />
      </form>
    );
  }
}

export default App;
